import "bootstrap/dist/css/bootstrap.css";
import { useState } from "react";
import { Dropdown, Button, Form } from "react-bootstrap";
import _ from "lodash";

export const DisplayGrid = () => {
  const [itemLocation, setItemLocation] = useState("");

  let gridValue = "";

  function chooseColumn(e) {
    gridValue = e.target.value;
  }

  function chooseRow(e) {
    if (_.isEmpty(gridValue)) {
      alert("Choose the column please!");
    }
    if (gridValue.length === 1) {
      const item = gridValue.concat(e.target.value);
      setItemLocation(item);
    }
  }

  return (
    <>
      <div className={"choose-product"}>
        <h2>Choose the item code </h2>
        <Button
          variant="primary"
          value="A"
          onClick={(e) => {
            chooseColumn(e);
          }}
        >
          A
        </Button>
        <Button
          variant="primary"
          value="B"
          onClick={(e) => {
            chooseColumn(e);
          }}
        >
          B
        </Button>
        <Button
          variant="primary"
          value="C"
          onClick={(e) => {
            chooseColumn(e);
          }}
        >
          C
        </Button>
        <br />
        <br />
        <div>
          <Button
            variant="outline-primary"
            value="1"
            onClick={(e) => {
              chooseRow(e);
            }}
          >
            1
          </Button>
          <Button
            variant="outline-primary"
            value="2"
            onClick={(e) => {
              chooseRow(e);
            }}
          >
            2
          </Button>
          <Button
            variant="outline-primary"
            value="3"
            onClick={(e) => {
              chooseRow(e);
            }}
          >
            3
          </Button>
          <Button
            variant="outline-primary"
            value="4"
            onClick={(e) => {
              chooseRow(e);
            }}
          >
            4
          </Button>
          <Button
            variant="outline-primary"
            value="5"
            onClick={(e) => {
              chooseRow(e);
            }}
          >
            5
          </Button>
        </div>
        <h2> you chose: {itemLocation} </h2>
      </div>
    </>
  );
};
