import "bootstrap/dist/css/bootstrap.css";
import { Dropdown, Button, Form } from "react-bootstrap";
import styles from "./products-on-display.modules.scss";

export const DisplayGrid = () => {
  function chooseRow(e) {
    console.log("row selected: ", e.target.value);
  }

  function chooseColumn(e) {
    console.log("column selected: ", e.target.value);
  }

  return (
    <>
      <div className={"choose-product"}>
        <h2>Display Grid </h2>
        <Button
          variant="primary"
          value="A"
          onClick={(e) => {
            chooseColumn(e);
          }}
        >
          A
        </Button>
        <Button
          variant="primary"
          value="B"
          onClick={(e) => {
            chooseColumn(e);
          }}
        >
          B
        </Button>
        <Button
          variant="primary"
          value="C"
          onClick={(e) => {
            chooseColumn(e);
          }}
        >
          C
        </Button>
        <br />
        <br />
        <div>
          <Button
            variant="outline-primary"
            value="1"
            onClick={(e) => {
              chooseRow(e);
            }}
          >
            1
          </Button>
          <Button variant="outline-primary">2</Button>
          <Button variant="outline-primary">3</Button>
          <Button variant="outline-primary">4</Button>
          <Button variant="outline-primary">5</Button>
        </div>
      </div>
    </>
  );
};
