import "bootstrap/dist/css/bootstrap.css";
import { useState } from "react";
import { Dropdown, Button, Form } from "react-bootstrap";
import { ProductsOnDisplay } from "./ProductsOnDisplay";

export const Machine = () => {
  const [balance, setBalance] = useState(0.0);
  //5 cents
  const [nickel, setNickel] = useState(0.0);
  //10 cents dime
  const [dime, setDime] = useState(0.0);
  const [quarter, setQuarter] = useState(0.0);
  const [curreny, setCurrency] = useState("");
  const [currencyAddition, setCurrencyAddition] = useState(false);

  function addCurrency(currency) {}

  return (
    <div
      className="container"
      style={{ marginLeft: "5%", textAlign: "center" }}
    >
      <h2>Total Balance: {balance}</h2>
      <h3 style={{ textAlign: "left" }}>
        {!currencyAddition && (
          <>
            <Button
              variant="outline-success"
              onClick={(e) => {
                e.preventDefault();
                setCurrencyAddition(true);
              }}
            >
              ADD CURRENCY
            </Button>
          </>
        )}
      </h3>
      {currencyAddition && (
        <Form
          className="add-currency"
          style={{ display: "flex", marginTop: "15%" }}
          onSubmit={() => {}}
        >
          <Form.Control style={{ width: "40%", marginRight: "3%" }} />
          <Dropdown
            onSelect={(e) => {
              e.preventDefault();
              console.log(e.target);
              setCurrency(e.target);
            }}
          >
            <Dropdown.Toggle variant="success" id="dropdown-basic">
              Choose Value
            </Dropdown.Toggle>
            <Dropdown.Menu>
              {" "}
              ><Dropdown.Item href="quarter">Quarter</Dropdown.Item>
              <Dropdown.Item href="dime">Dime</Dropdown.Item>
              <Dropdown.Item href="nickel">Nickel</Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>

          <Button style={{ marginLeft: "1%" }} type="submit">
            Add
          </Button>
        </Form>
      )}
      <br />
      <br />
      <ProductsOnDisplay />
      hello
    </div>
  );
};
