import "bootstrap/dist/css/bootstrap.css";
import { Dropdown, Button, Form } from "react-bootstrap";
import styles from "./products-on-display.modules.scss";

export const DisplayGrid = () => {
  return (
    <>
      <div className={"choose-product"}>
        <h2>Display Grid </h2>
        <Button
          variant="primary"
          value="A"
          onClick={(e) => {
            console.log(e.target.value);
          }}
        >
          A
        </Button>
        <Button variant="primary">B</Button>
        <Button variant="primary">C</Button>
        <br />
        <br />
        <div>
          <Button variant="outline-primary">1</Button>
          <Button variant="outline-primary">2</Button>
          <Button variant="outline-primary">3</Button>
          <Button variant="outline-primary">4</Button>
          <Button variant="outline-primary">5</Button>
        </div>
      </div>
    </>
  );
};
